// Helper function to format text with proper HTML structure
function formatSectionText(text) {
    if (!text) return '';

    // Split by colons followed by capital letters (section headers)
    let formatted = text
        // Add paragraph breaks before section headers (word followed by colon)
        .replace(/([.!?])\s+([A-Z][^:]+:)/g, '$1</p><p><strong>$2</strong>')
        // Add line breaks after colons that start sections
        .replace(/([A-Z][^:]+:)\s+/g, '<strong>$1</strong><br>')
        // Wrap in initial paragraph tag
        .replace(/^/, '<p>')
        .replace(/$/, '</p>')
        // Clean up any empty paragraphs
        .replace(/<p>\s*<\/p>/g, '');

    return formatted;
}

