// Generate breadcrumb navigation
function generateBreadcrumbs(path) {
    const parts = path.map((part, index) => {
        if (index === path.length - 1) {
            // Last item (current page) - no link
            return `<span class="current-page">${part.name}</span>`;
        } else {
            // Clickable links
            return `<a href="javascript:void(0)" onclick="${part.onclick}">${part.name}</a>`;
        }
    });

    return `<div class="wiki-breadcrumbs">${parts.join('<span>‚Ä∫</span>')}</div>`;
}

// Generate character infobox
function generateInfobox(character, type = 'child') {
    const imageMap = {
        // Children
        'Jacthina': 'Jacthina.png',
        'Kabina': 'Kabina.png',
        'Seraphina': 'Seraphina.png',
        'Valiyah': 'Valiyah.png',
        'Tatum': 'Tatum.png',
        'Mirare': 'Mirare.png',
        'Airam': 'Airam.png',
        // Inner Circle
        'Maui': 'Maui.png',
        'Naymor': 'Naymor.png',
        'Zendayah': 'Zendayah.png',
        'Naime': 'Naime.png',
        'Siddiq': 'Siddiq.png'
    };

    const imagePath = imageMap[character.name];
    const placeholder = type === 'protector' ? '‚öî' : '‚ú®';
    const imageHTML = imagePath ?
        `<img src="Images/${imagePath}" alt="${character.name}" onerror="this.parentElement.innerHTML='<div class=\\'wiki-infobox-placeholder\\'>${placeholder}</div>'">` :
        `<div class="wiki-infobox-placeholder">${placeholder}</div>`;

    // Extract key info from title
    let divineStatus = 'Unknown';
    let fullName = character.name;

    if (character.title) {
        const statusMatch = character.title.match(/Divine Status:\s*([^|]+)/);
        if (statusMatch) divineStatus = statusMatch[1].trim();

        const nameMatch = character.title.match(/Full Name:\s*([^|]+)/);
        if (nameMatch) fullName = nameMatch[1].trim();
    }

    // Build infobox rows based on type
    let infoRows = '';

    if (character.subtitle) {
        infoRows += `
            <div class="wiki-infobox-row">
                <div class="wiki-infobox-label">Title</div>
                <div class="wiki-infobox-value">${character.subtitle}</div>
            </div>`;
    }

    infoRows += `
        <div class="wiki-infobox-row">
            <div class="wiki-infobox-label">${type === 'child' ? 'Full Name' : 'Name'}</div>
            <div class="wiki-infobox-value">${fullName}</div>
        </div>
        <div class="wiki-infobox-row">
            <div class="wiki-infobox-label">Status</div>
            <div class="wiki-infobox-value">${divineStatus}</div>
        </div>`;

    if (type === 'child') {
        infoRows += `
            <div class="wiki-infobox-row">
                <div class="wiki-infobox-label">Mother</div>
                <div class="wiki-infobox-value"><a href="javascript:void(0)" class="wiki-link" onclick="showEudoraProfile()">Eudora</a></div>
            </div>`;
    } else if (type === 'protector') {
        infoRows += `
            <div class="wiki-infobox-row">
                <div class="wiki-infobox-label">Affiliation</div>
                <div class="wiki-infobox-value">Eudora's Inner Circle</div>
            </div>`;
    }

    return `
        <aside class="wiki-infobox">
            <div class="wiki-infobox-header">${character.name}</div>
            <div class="wiki-infobox-image">${imageHTML}</div>
            <div class="wiki-infobox-body">
                ${infoRows}
            </div>
        </aside>
    `;
}

// Generate table of contents
function generateTOC(sections) {
    const items = sections.map((section, index) =>
        `<li><a href="#wiki-section-${index}">${section}</a></li>`
    ).join('');

    return `
        <div class="wiki-toc">
            <div class="wiki-toc-title">
                <span class="wiki-toc-toggle" onclick="toggleTOC(this)">[hide]</span>
                <span>Contents</span>
            </div>
            <div class="wiki-toc-content">
                <ol>${items}</ol>
            </div>
        </div>
    `;
}

// Toggle TOC visibility
function toggleTOC(button) {
    const content = button.closest('.wiki-toc').querySelector('.wiki-toc-content');
    content.classList.toggle('collapsed');
    button.textContent = content.classList.contains('collapsed') ? '[show]' : '[hide]';
}

// Generate "See Also" section
function generateSeeAlso(links) {
    const items = links.map(link =>
        `<li><a href="javascript:void(0)" class="wiki-link" onclick="${link.onclick}">${link.name}</a></li>`
    ).join('');

    return `
        <div class="wiki-see-also">
            <h3>See Also</h3>
            <ul>${items}</ul>
        </div>
    `;
}

